package baitapIO;import java.io.Serializable;public class Person implements Serializable {    private String name;    private int age;    private String gender;    private String phone;    public Person(String name, int age, String gender, String phone) {        this.name = name;        this.age = age;        this.gender = gender;        this.phone = phone;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public int getAge() {        return age;    }    public void setAge(int age) {        this.age = age;    }    public String getGioitinh() {        return gender;    }    public void setGioitinh(String gioitinh) {        this.gender = gioitinh;    }    public String getSdt() {        return phone;    }    public void setSdt(String sdt) {        this.phone = sdt;    }    public String show() {        return name + " , " + age + " , " + gender + " , " + phone;    }}